using System.Collections.Generic;using UnityEngine;using Newtonsoft.Json;public class CharacterConfig  {		public int id { get; set; }		public int name { get; set; }		public  List<int> remark { get; set; }		public string res { get; set; }		public string targetRes { get; set; }		public string dottedline { get; set; }		public  List<string> solidline { get; set; }		public string hook { get; set; }		public int getType { get; set; }		public int spend { get; set; }		public bool isshop { get; set; }		public bool hide { get; set; }		 		public static string configName = "CharacterConfig";		public static CharacterConfig config { get; set; }		public string version { get; set; }		public List<CharacterConfig> datas { get; set; }		public static void Init()		{			ConfigManager.Ins.Readjson(configName, (json) => {			if (!string.IsNullOrEmpty( json))			{				 config = JsonConvert.DeserializeObject<CharacterConfig>(json);				 PlayerPrefs.SetString("brother_" + configName, config.version);			}			}, false);		 }		public static CharacterConfig Get(int id)		{			foreach (var item in config.datas)			{				if (item.id == id)				{ 					return item;				}			}			return null;		}}