using System.Collections.Generic;using UnityEngine;using Newtonsoft.Json;public class ClearanceConfig  {		public int id { get; set; }		public int remark { get; set; }		public int clearanceType { get; set; }		 		public static string configName = "ClearanceConfig";		public static ClearanceConfig config { get; set; }		public string version { get; set; }		public List<ClearanceConfig> datas { get; set; }		public static void Init()		{			ConfigManager.Ins.Readjson(configName, (json) => {			if (!string.IsNullOrEmpty( json))			{				 config = JsonConvert.DeserializeObject<ClearanceConfig>(json);				 PlayerPrefs.SetString("brother_" + configName, config.version);			}			}, false);		 }		public static ClearanceConfig Get(int id)		{			foreach (var item in config.datas)			{				if (item.id == id)				{ 					return item;				}			}			return null;		}}