using System.Collections.Generic;using UnityEngine;using Newtonsoft.Json;public class GuideConfig  {		public int id { get; set; }		public string position { get; set; }		public string textType { get; set; }		public int radius { get; set; }		public int strid { get; set; }		public bool isHide { get; set; }		public bool isUI { get; set; }		 		public static string configName = "GuideConfig";		public static GuideConfig config { get; set; }		public string version { get; set; }		public List<GuideConfig> datas { get; set; }		public static void Init()		{			ConfigManager.Ins.Readjson(configName, (json) => {			if (!string.IsNullOrEmpty( json))			{				 config = JsonConvert.DeserializeObject<GuideConfig>(json);				 PlayerPrefs.SetString("brother_" + configName, config.version);			}			}, false);		 }		public static GuideConfig Get(int id)		{			foreach (var item in config.datas)			{				if (item.id == id)				{ 					return item;				}			}			return null;		}}