using System.Collections.Generic;using UnityEngine;using Newtonsoft.Json;public class RewardConfig  {		public int id { get; set; }		public int condition { get; set; }		public int type { get; set; }		public int count { get; set; }		public string icon { get; set; }		 		public static string configName = "RewardConfig";		public static RewardConfig config { get; set; }		public string version { get; set; }		public List<RewardConfig> datas { get; set; }		public static void Init()		{			ConfigManager.Ins.Readjson(configName, (json) => {			if (!string.IsNullOrEmpty( json))			{				 config = JsonConvert.DeserializeObject<RewardConfig>(json);				 PlayerPrefs.SetString("brother_" + configName, config.version);			}			}, false);		 }		public static RewardConfig Get(int id)		{			foreach (var item in config.datas)			{				if (item.id == id)				{ 					return item;				}			}			return null;		}}