using System.Collections.Generic;using UnityEngine;using Newtonsoft.Json;public class SceneConfig  {		public int id { get; set; }		public int name { get; set; }		public int remark { get; set; }		public string prefabName { get; set; }		public string mainuiRes { get; set; }		public string level { get; set; }		public string rewards { get; set; }		public int spend { get; set; }		 		public static string configName = "SceneConfig";		public static SceneConfig config { get; set; }		public string version { get; set; }		public List<SceneConfig> datas { get; set; }		public static void Init()		{			ConfigManager.Ins.Readjson(configName, (json) => {			if (!string.IsNullOrEmpty( json))			{				 config = JsonConvert.DeserializeObject<SceneConfig>(json);				 PlayerPrefs.SetString("brother_" + configName, config.version);			}			}, false);		 }		public static SceneConfig Get(int id)		{			foreach (var item in config.datas)			{				if (item.id == id)				{ 					return item;				}			}			return null;		}}