using System.Collections.Generic;using UnityEngine;using Newtonsoft.Json;public class SmearConfig  {		public int id { get; set; }		public int name { get; set; }		public int remark { get; set; }		public string res { get; set; }		public int getType { get; set; }		public int spend { get; set; }		public bool isshop { get; set; }		public bool hide { get; set; }		 		public static string configName = "SmearConfig";		public static SmearConfig config { get; set; }		public string version { get; set; }		public List<SmearConfig> datas { get; set; }		public static void Init()		{			ConfigManager.Ins.Readjson(configName, (json) => {			if (!string.IsNullOrEmpty( json))			{				 config = JsonConvert.DeserializeObject<SmearConfig>(json);				 PlayerPrefs.SetString("brother_" + configName, config.version);			}			}, false);		 }		public static SmearConfig Get(int id)		{			foreach (var item in config.datas)			{				if (item.id == id)				{ 					return item;				}			}			return null;		}}