using System.Collections.Generic;using UnityEngine;using Newtonsoft.Json;public class SystemConfig  {		public int id { get; set; }		public float randNum { get; set; }		public float randNumSpc { get; set; }		 		public static string configName = "SystemConfig";		public static SystemConfig config { get; set; }		public string version { get; set; }		public List<SystemConfig> datas { get; set; }		public static void Init()		{			ConfigManager.Ins.Readjson(configName, (json) => {			if (!string.IsNullOrEmpty( json))			{				 config = JsonConvert.DeserializeObject<SystemConfig>(json);				 PlayerPrefs.SetString("brother_" + configName, config.version);			}			}, false);		 }		public static SystemConfig Get(int id)		{			foreach (var item in config.datas)			{				if (item.id == id)				{ 					return item;				}			}			return null;		}}