using System.Collections.Generic;using UnityEngine;using Newtonsoft.Json;public class TurnTableConfig  {		public int id { get; set; }		public int type { get; set; }		public int count { get; set; }		 		public static string configName = "TurnTableConfig";		public static TurnTableConfig config { get; set; }		public string version { get; set; }		public List<TurnTableConfig> datas { get; set; }		public static void Init()		{			ConfigManager.Ins.Readjson(configName, (json) => {			if (!string.IsNullOrEmpty( json))			{				 config = JsonConvert.DeserializeObject<TurnTableConfig>(json);				 PlayerPrefs.SetString("brother_" + configName, config.version);			}			}, false);		 }		public static TurnTableConfig Get(int id)		{			foreach (var item in config.datas)			{				if (item.id == id)				{ 					return item;				}			}			return null;		}}